craftAmethystBlock.code:
async function craftAmethystBlock(bot) {
    // Check if there are enough amethyst shards in the inventory
    let amethystsCount = bot.inventory.count(mcData.itemsByName.amethyst_shard.id);
    // If not enough amethyst shards, mine some
    while (amethystsCount < 4) {
      await mineAmethystCluster(bot);
      amethystsCount = bot.inventory.count(mcData.itemsByName.amethyst.id);
    }

    // Check if crafting table is in the inventory
    const craftingTableCount = bot.inventory.count(mcData.itemsByName.crafting_table.id);
    // If not, craft a crafting table
    if (craftingTableCount === 0) {
      await craftCraftingTable(bot);
    }
    // Place the crafting table near the bot
    const craftingTablePosition = await findSuitablePosition(bot);
    await placeItem(bot, "crafting_table", craftingTablePosition);
    // Craft an amethyst block using the crafting table
    await craftItem(bot, "amethyst_block", 1);
    bot.chat("Crafted an amethyst block.");
}

craftAnvil.code:
async function craftAnvil(bot) {
    await smeltAllRawIron(bot);
    // Check if there are enough iron ingots and iron blocks in the inventory
    let ironBlockCount = bot.inventory.count(mcData.itemsByName.iron_block.id);
    // If not enough iron_block, craft some
    if (ironBlockCount < 3) {
        await craftIronBlock(bot);
    }
    let ironIngotsCount = bot.inventory.count(mcData.itemsByName.iron_ingot.id);
    // If not enough iron ingots, mine iron ores and smelt them into iron ingots
    while (ironIngotsCount < 4) {
      await mineIronOre(bot);
      ironIngotsCount += 1;
    }
    await smeltAllRawIron(bot);
    // Check if crafting table is in the inventory
    const craftingTableCount = bot.inventory.count(mcData.itemsByName.crafting_table.id);
    // If not, craft a crafting table
    if (craftingTableCount === 0) {
      await craftCraftingTable(bot);
    }
    // Place the crafting table near the bot
